INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(1, 1, '[WellBe] Received your booking. %v', 'Thank you for your interest in WellBe.\r\nWe have accepted your booking as follows.\r\nPlease check the details and come to the salon on the booking date and time.\r\nPlease note that this e-mail is for delivery only and will not be sent to the salon even if you reply to it.\r\n\r\n- Booking Information\r\n   Booking number：%v\r\n   Booking store：%v\r\n   Address：%v\r\n   Booking date and time：%v(local time)\r\n\r\n%v- Cancellation Policy\r\nPlease visit https://wellbe-asia.com/cancellation-policy if you want check for cancellation details.\r\n\r\n\r\nWellBe', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(2, 1, '[WellBe] Canceled your booking to %v. %v', 'Thank you for your interest in WellBe.\r\nWe have accepted your cancellation of your booking.\r\nWe look forward to serving you again.\r\n\r\n- Booking Information\r\n\r\nBooking number: %v\r\nBooking date and time: %v (local time)\r\nBooking Store：%v\r\n\r\n\r\n- Refund Amount\r\n\r\n%v %v\r\n\r\nBreakdown\r\nPayment amount: %v %v\r\nCancellation fee: %v %v\r\n\r\nRefunds will be processed sequentially by the system.\r\nIn addition, in the case of credit card payment, cancellation processing on the credit card statement may take several business days due to the convenience of the credit card company.\r\nPlease understand.\r\n\r\n\r\nWellBe', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(3, 1, '[WellBe] Canceled your booking to %v. %v', 'Thank you for visiting WellBe.\r\nWe have cancelled your booking because the payment deadline has passed.\r\n\r\n\r\n- Booking Information\r\n\r\nBooking number: %v\r\nBooking date and time：%v(local time)\r\nBooking store：%v\r\n\r\n\r\nWe look forward to serving you again.\r\n\r\n\r\nWellBe', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(4, 1, '[WellBe] We have sent payment for the sales amount.', 'Dear Partner Store\r\n\r\nThank you for using WellBe.\r\nWe have sent you payment for the amount of sales up to %v.\r\nPlease bill us through your Paypal account.', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(5, 1, '[WellBe] Payment failed.', 'Thank you for your interest in WellBe.\r\nThe payment for the following booking failed due to the credit card company or payment company.\r\nWe apologize for any inconvenience caused, and ask you to try the payment again.\r\n\r\n- Booking Information\r\n   Booking number: %v\r\n   Booking store：%v\r\n\r\n\r\n- Payment Amount\r\n\r\n   %v %v\r\n\r\n\r\nThe payment screen can be viewed from Menu > Account Information.', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(6, 1, '[WellBe] Request your booking. %v', 'Thank you for your interest in. WellBe.\r\nWe have accepted your booking as follows.\r\n If you reply to this email, it will not be sent to the salon.\r\nWe will contact you with in 15 hours to confirm or deny your booking to your registered email address. \r\nPlease check your email if you do not receive the email after 15 hours, as it may have been sorted into your junk mail.  \r\n\r\n- Booking Information\r\n   Booking number：%v\r\n   Booking store：%v\r\n   Address：%v\r\n   Booking date and time：%v(local time)\r\n\r\n%v- Cancellation Policy\r\nPlease visit https://wellbe-asia.com/cancellation-policy If you have already paid for your booking, please do not cancel it.\r\n\r\n\r\nWellBe', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(7, 1, '[WellBe] Your account registration has been completed.', 'Thank you for registering with WellBe.\r\n\r\nPlease login to the member page from the following URL to check your registration information and booking status.\r\n\r\nMember Page\r\nhttps://wellbe-asia.com/account/\r\n\r\nClick here to find a salon\r\nhttps://wellbe-asia.com/\r\n\r\nThis email is sent from a dedicated email address. Please note that we will not be able to respond to your response.\r\n\r\n\r\nWellBe', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(8, 1, '[WellBe] Withdrawal procedure has been completed.', 'Your withdrawal from WellBe has been accepted.\r\n\r\nThank you for using WellBe so far.\r\nWe look forward to seeing you again.\r\n\r\nPlease note that this email is sent from a dedicated email address. Please note that we will not be able to respond to your reply.\r\nPlease note that we will not be able to respond to your reply.\r\n\r\n\r\nWellBe', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(9, 1, '[WellBe] Canceled your booking to %v. %v', 'Thank you for visiting WellBe.\r\nWe regret to inform you that your booking has been cancelled by the salon.\r\nWe apologize for any inconvenience caused.\r\nWe look forward to serving you again.\r\n\r\n- Booking Information\r\n\r\nBooking number: %v\r\nBooking date and time: %v (local time)\r\nBooking Store：%v\r\n\r\n\r\n- Refund Amount\r\n\r\n%v %v\r\n\r\nBreakdown\r\n-Payment amount: %v %v\r\n-Cancellation fee: %v %v\r\n\r\nRefunds will be processed sequentially by the system.\r\nIn addition, in the case of credit card payment, cancellation processing on the credit card statement may take several business days due to the convenience of the credit card company.\r\nPlease understand.\r\n\r\n\r\n\r\n--\r\n- Reason for Cancellation\r\n%v\r\n\r\n\r\nWellBe', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(10, 1, '[WellBe] Canceled your booking to %v. %v', 'Thank you for visiting WellBe.\r\nWe are sorry to inform you that your reservation was automatically cancelled as it was not accepted by the store for a certain period of time.\r\nWe apologize for any inconvenience caused.\r\nWe look forward to serving you again.\r\n\r\n\r\n- Reservation Information\r\nReservation number: %v\r\nReservation date and time：%v(local time)\r\nReservation Store：%v\r\n\r\n\r\nWellBe', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(11, 1, '[WellBe] Request your booking. %v', 'Thank you for visiting WellBe.\r\nYou accepted a booking request.\r\n\r\nPlease reply to the booking availability within 15 hours from the management screen. Please note that your booking will be automatically cancelled after the deadline.\r\n\r\n- Booking Information\r\nBooking number: %v\r\nBooking date and time: %v \r\nBooking menu：%v\r\nNumber of persons: %v\r\nBooking amount: %v %v\r\n\r\nPlease make a booking from the store management screen.\r\nhttps://shop.wellbe-asia.com/booking/\r\n\r\nCancellation Policy\r\nPlease contact us at https://shop.wellbe-asia.com/terms for our cancellation policy after your booking is confirmed.', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(12, 1, '[WellBe] Booking accepted. %v', 'Thank you for visiting WellBe.\r\nYou accepted a booking.\r\n\r\n- Booking Information\r\nBooking number: %v\r\nBooking date and time: %v \r\nBooking Menu: %v\r\nNumber of persons: %v %v\r\nBooking amount: %v %v\r\n\r\n\r\nCancellation Policy\r\nPlease contact us at https://shop.wellbe-asia.com/terms for details on cancellation policy after the booking is confirmed.', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(13, 1, '[WellBe] Booking confirmed. %v', 'Thank you for using WellBe.\r\nYour request reservation has been accepted.\r\nPlease check the details and come to the store on your reserved date and time.\r\n*This email is for distribution only, so even if you reply, it will not be delivered to the salon.\r\n\r\n- Reservation information\r\nReservation number: %v\r\nReservation store:%v\r\nAddress:%v\r\nReservation date and time: %v (local time)\r\n\r\n%v\r\n\r\n- Cancellation policy\r\nFor cancellation details, please see https://wellbe-asia.com/cancellation-policy.\r\n\r\n\r\nWellBe', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(14, 1, '[WellBe] User inquiry accepted [%v].', 'Inquiry received from user.\r\n\r\n- Name:\r\n%v\r\n\r\n- E-mail address:\r\n%v\r\n\r\n- Reservation No.: %v\r\n%v\r\n\r\n- Inquiry type: %v\r\n%v\r\n\r\n- Subject:\r\n%v\r\n\r\n- Body:\r\n%v\r\n\r\n\r\nWellBe\r\n', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(15, 1, '[WellBe] Please feedback your experience of %v!', 'Thank you for your using WellBe.\r\n\r\nWe are looking for feedback from you.\r\nIf you would like, we would be happy to post a review of your experience.\r\nYou have 30 days from the date of your visit to submit a review.\r\nYou can enter your feedback below.\r\n\r\nhttps://wellbe-asia.com/account/review\r\n\r\nWe look forward to serving you again.\r\n\r\nWellBe', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(16, 1, '[WellBe] Please complete the payment or booking will be cancelled. %v', 'Thank you for your booking.\r\nYour reservation has not yet been settled.\r\nPlease make your payment by the due date.\r\nIf payment is not made by the due date, your booking will be automatically cancelled. \r\n\r\nPayment can be made from the following\r\nhttps://wellbe-asia.com/en/booking/checkout/%v/\r\n\r\n- Booking Information\r\nBooking number：%v\r\nBooking store：%v\r\nAddress：%v\r\nBooking date and time：%v (local time)\r\n\r\n- Payment Amount\r\n%v %v\r\n\r\n- Payment Due Date\r\n%v (local time)\r\n\r\nPlease note that your booking will be automatically cancelled after the deadline if the payment has not been made. \If you have already paid, please let it go.\r\nIf you have already paid, please let it go.\r\n\r\n- Cancellation Policy\r\nPlease visit https://wellbe-asia.com/cancellation-policy If you have already paid for your booking, please do not cancel it.\r\n\r\n\r\nWellBe\r\n', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(17, 1, '[WellBe] Reservation canceled. %v', 'Thank you for always using WellBe. \r\nThe customer''s reservation has been cancelled. \r\n\r\n■Reservation information\r\nReservation number: %v\r\nReservation date and time: %v \r\nReservation menu: %v\r\nNumber of people reserved: %v \r\ nReservation amount: %v %v\r\nCancellation date and time: %v\r\n\r\nWellBe', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(1, 2, '[WellBe] 予約を受付けました。 %v', 'この度はWellBeをご利用いただきましてありがとうございます。\r\n以下のとおりご予約を受付けました。\r\n内容をご確認いただきご予約日時にご来店ください。\r\n※本メールは配信専用のため、ご返信いただきましてもサロンへは届きません。\r\n\r\n■予約情報\r\n　予約番号：%v\r\n　予約店舗：%v\r\n　住所：%v\r\n　予約日時：%v（現地時間）\r\n\r\n%v■キャンセルポリシー\r\nキャンセルの詳細はhttps://wellbe-asia.com/cancellation-policyをご確認ください。\r\n\r\n\r\nWellBe', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(2, 2, '[WellBe] %vの予約をキャンセルしました。 %v', 'この度はWellBeをご利用いただきありがとうございます。\r\nご予約のキャンセルを受付けました。\r\nまたのご利用お待ちしております。\r\n\r\n■予約情報\r\n\r\n予約番号：%v\r\n予約日時：%v（現地時間）\r\n予約店舗：%v\r\n\r\n\r\n■返金額\r\n\r\n%v %v\r\n\r\n内訳\r\n-決済金額：%v %v\r\n-キャンセル料：%v %v\r\n\r\n※返金処理はシステムで順次実施いたします。\r\nまた、クレジットカード決済の場合は、カード会社の都合で利用明細上の取消処理が数営業日後になる場合があります。\r\nご了承ください。\r\n\r\nWellBe', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(3, 2, '[WellBe] %vの予約をキャンセルしました。 %v', 'この度はWellBeをご利用いただきありがとうございます。\r\nお支払い期限が過ぎたため、お客様のご予約をキャンセルいたしました。\r\n\r\n\r\n■予約情報\r\n\r\n予約番号：%v\r\n予約日時：%v（現地時間）\r\n予約店舗：%v\r\n\r\n\r\nまたのご利用をお待ちしております。\r\n\r\n\r\nWellBe', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(4, 2, '[WellBe] 売上金額のお支払を送付いたしました。', 'パートナー店舗様\r\n\r\nこの度はWellBeをご利用いただきありがとうございます。\r\n%vまでの売上金額のお支払いを送付いたしました。\r\nPaypalアカウントで請求を行ってください。\r\n', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(5, 2, '[WellBe] 決済が失敗しました。 %v', 'この度はWellBeをご利用いただきありがとうございます。\r\n以下のご予約の決済が、クレジットカード会社もしくは決済会社の都合で失敗しました。\r\nご不便をおかけして申し訳ありませんが、再度決済を行ってください。\r\n\r\n■予約情報\r\n\r\n　予約番号：%v\r\n　予約店舗：%v\r\n\r\n\r\n■料金\r\n\r\n　%v %v\r\n\r\n\r\n決済画面はメニュー＞アカウント情報よりご覧いただけます。', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(6, 2, '[WellBe] リクエスト予約を受付けました。 %v', 'この度はWellBeをご利用いただきましてありがとうございます。\r\n以下のとおりご予約を受付けました。\r\n※本メールは配信専用のため、ご返信いただきましてもサロンへは届きません。\r\n\r\nこちらのサロンはリクエスト予約です。\r\n15時間以内に予約可否のご連絡をご登録のメールアドレス宛に送付いたします。\r\nなお、15時間経過してもメールが届かない場合は、迷惑メールに振り分けられる可能性がございますのでご確認ください。\r\n\r\n\r\n■予約情報\r\n　予約番号：%v\r\n　予約店舗：%v\r\n　住所：%v\r\n　予約日時：%v（現地時間）\r\n\r\n%v■キャンセルポリシー\r\nキャンセルの詳細はhttps://wellbe-asia.com/cancellation-policyをご確認ください。\r\n\r\n\r\nWellBe', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(7, 2, '[WellBe] 会員登録が完了しました。', 'このたびはWellBeにご登録いただき誠にありがとうございます。\r\n\r\n以下のURLから会員ページにログインし、ご登録情報および予約状況等のご確認をお願いします。\r\n\r\n▼会員ページ\r\nhttps://wellbe-asia.com/ja/account/\r\n\r\n▼サロン探しはこちらから\r\nhttps://wellbe-asia.com/\r\n\r\n※本メールは送信専用メールアドレスから配信されています。ご返信いただいてもお答えできませんのでご了承ください。\r\n\r\n\r\nWellBe', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(8, 2, '[WellBe] 退会手続きが完了しました。', '退会手続きが完了いたしました。\r\n\r\nWellBeをご利用いただきありがとうございました。\r\n\r\n※本メールは送信専用メールアドレスから配信されています。ご返信いただいてもお答えできませんのでご了承ください。\r\n※退会後、自動的にメール配信は停止されますが、情報更新のタイミングにより退会後もメールが数通配信される場合があります。\r\n\r\n\r\nWellBe', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(9, 2, '[WellBe] %vの予約をキャンセルしました。 %v', 'この度はWellBeをご利用いただきありがとうございます。\r\n誠に申し訳ございませんが、お客様のご予約は店舗側の都合で予約がキャンセルされました。\r\nご不便をおかけして申し訳ございません。\r\nまたのご利用お待ちしております。\r\n\r\n■予約情報\r\n\r\n予約番号：%v\r\n予約日時：%v（現地時間）\r\n予約店舗：%v\r\n\r\n\r\n■返金額\r\n\r\n%v %v\r\n\r\n内訳\r\n-決済金額：%v %v\r\n-キャンセル料：%v %v\r\n\r\n※返金処理はシステムで順次実施いたします。\r\nまた、クレジットカード決済の場合は、カード会社の都合で利用明細上の取消処理が数営業日後になる場合があります。\r\nご了承ください。\r\n\r\n\r\n\r\n--\r\n■キャンセル理由\r\n%v\r\n\r\n\r\nWellBe', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(10, 2, '[WellBe] %vの予約をキャンセルしました。 %v', 'この度はWellBeをご利用いただきありがとうございます。\r\n誠に申し訳ございませんが、お客様のご予約は店舗側で一定期間受付されなかったため自動キャンセルされました。\r\nご不便をおかけして申し訳ございません。\r\nまたのご利用お待ちしております。\r\n\r\n\r\n■予約情報\r\n予約番号：%v\r\n予約日時：%v（現地時間）\r\n予約店舗：%v\r\n\r\n\r\nWellBe', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(11, 2, '[WellBe] リクエスト予約を受付けました。 %v', 'いつもWellBeをご利用いただきありがとうございます。\r\nお客様からのリクエスト予約を受付けました。\r\n\r\n管理画面から15時間以内に予約可否を回答してください。期限を過ぎますと予約が自動キャンセルとなりますのでご注意ください。\r\n\r\n■予約情報\r\n予約番号：%v\r\n予約日時：%v \r\n予約メニュー：%v\r\n予約人数：%v 名\r\n予約金額：%v %v\r\n\r\n店舗管理画面より予約受付を行ってください。\r\nhttps://shop.wellbe-asia.com/booking/\r\n\r\n■キャンセルポリシー\r\n予約確定後の店鋪都合のキャンセルの詳細はhttps://shop.wellbe-asia.com/terms をご確認ください。\r\n\r\n\r\nWellBe', '2001/1/1 0:00', 'DML', '', '');
