INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(1, 3, '[WellBe] Đặt phòng của bạn đã được chấp nhận. %v', 'Cảm ơn bạn đã đặt phòng WellBe.\r\nChúng tôi đã chấp nhận đặt phòng của bạn như sau.\r\nVui lòng kiểm tra thông tin chi tiết và đến thẩm mỹ viện vào ngày giờ đã đặt trước.\r\nXin lưu ý rằng e-mail này chỉ dành cho mục đích giao hàng và sẽ không được gửi đến tiệm ngay cả khi bạn trả lời.\r\n\r\n- Thông tin đặt phòng\r\n    Số đặt trước：%v\r\n    Cửa hàng đặt trước：%v\r\n    Địa chỉ：%v\r\n    Ngày giờ đặt trước：%v(giờ địa phương)\r\n\r\n%v- Chính sách hủy\r\nVui lòng truy cập https://wellbe-asia.com/cancellation-policy nếu bạn muốn kiểm tra chi tiết hủy bỏ.\r\n\r\n\r\nnWellBe', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(2, 3, '[WellBe] Đã hủy đặt chỗ của bạn tới %v. %v', 'Cảm ơn bạn đã quan tâm đến WellBe.\r\nChúng tôi đã chấp nhận việc hủy đặt phòng của bạn.\r\nChúng tôi mong được phục vụ bạn một lần nữa.\r\n\r\n- Thông tin đặt phòng\r\n\r\nSố đặt trước: %v\r\nNgày và giờ đặt trước: %v (giờ địa phương)\r\nCửa hàng đặt chỗ:%v\r\n\r\n\r\n- Số tiền hoàn trả\r\n\r\n%v %v\r\n\r\nPhá vỡ\r\nSố tiền thanh toán: %v %v\r\nPhí hủy: %v %v\r\n\r\nHoàn tiền sẽ được hệ thống xử lý tuần tự.\r\nNgoài ra, trong trường hợp thanh toán bằng thẻ tín dụng, quá trình hủy bỏ trên bảng sao kê thẻ tín dụng có thể mất vài ngày làm việc do sự tiện lợi của công ty thẻ tín dụng.\r\nLàm ơn hãy hiểu.\r\n\r\n\r\nWellBe', '2001/1/1 0:00', 'DML', '', '');
