INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(4, 3, '[WellBe] Chúng tôi đã gửi thanh toán cho số tiền bán hàng.', 'Kính gửi cửa hàng đối tác\r\n\r\nCảm ơn bạn đã sử dụng WellBe.\r\nChúng tôi đã gửi cho bạn khoản thanh toán cho số tiền bán hàng lên tới %v.\r\nVui lòng gửi hóa đơn cho chúng tôi qua tài khoản Paypal của bạn.', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(5, 3, '[WellBe] Thanh toán không thành công. ', 'Cảm ơn bạn đã quan tâm đến WellBe.\r\nThanh toán cho đặt phòng sau không thành công do công ty thẻ tín dụng hoặc công ty thanh toán.\r\nChúng tôi xin lỗi vì bất kỳ sự bất tiện nào đã gây ra và yêu cầu bạn thử thanh toán lại.\r\n\r\n- Thông tin đặt phòng\r\n    Số đặt trước: %v\r\n    Cửa hàng đặt trước：%v\r\n\r\n\r\n- Số tiền thanh toán\r\n\r\n    %v %v\r\n\r\n\r\nCó thể xem màn hình thanh toán từ Menu > Thông tin tài khoản.', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(6, 3, '[WellBe] Yêu cầu đặt phòng của bạn đã được chấp nhận. %v', 'Cảm ơn bạn đã đặt phòng WellBe.\r\nXin lưu ý rằng các câu trả lời cho email này sẽ không được gửi đến thẩm mỹ viện.\r\n\r\n-Chúng tôi sẽ liên hệ với bạn trong vòng 15 giờ tới địa chỉ email đã đăng ký của bạn về tính khả dụng của đặt phòng của bạn. Nếu bạn không nhận được email sau 15 giờ, vui lòng kiểm tra email của bạn. Nó có thể đã được phân.\r\n  Số đặt trước：%v\r\n    Cửa hàng đặt trước：%v\r\n    Địa chỉ：%v\r\n    Ngày giờ đặt trước：%v(giờ địa phương)\r\n\r\n%v- Chính sách hủy\r\nVui lòng truy cập https://wellbe-asia.com/cancellation-policy Nếu bạn muốn biết thêm thông tin về việc hủy, vui lòng xem Chính sách hủy của chúng tôi.', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(7, 3, '[WellBe] Đăng ký tài khoản của bạn đã được hoàn thành.', 'Cảm ơn bạn đã đăng ký với WellBe.\r\n\r\nVui lòng đăng nhập vào trang thành viên từ URL sau để kiểm tra thông tin đăng ký và trạng thái đặt phòng của bạn.\r\n\r\nTrang thành viên\r\nhttps://wellbe-asia.com/account/\r\n\r\nNhấn vào đây để tìm một thẩm mỹ viện\r\nhttps://wellbe-asia.com/\r\n\r\nEmail này được gửi từ một địa chỉ email chuyên dụng. Xin lưu ý rằng chúng tôi sẽ không thể trả lời phản hồi của bạn.\r\n\r\n\r\nCũng được', '2001/1/1 0:00', 'DML', '', '');
