INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(15, 3, '[WellBe] Hãy phản hồi trải nghiệm của bạn về %v nhé!', 'Cảm ơn bạn đã sử dụng WellBe.\r\n\r\nChúng tôi đang tìm kiếm phản hồi từ bạn.\r\nNếu bạn muốn, chúng tôi rất sẵn lòng đăng bài đánh giá về trải nghiệm của bạn.\r\nBạn có 30 ngày kể từ ngày bạn ghé thăm để gửi đánh giá.\r\nBạn có thể nhập phản hồi của bạn dưới đây.\r\n\r\nhttps://wellbe-asia.com/account/review\r\n\r\nChúng tôi mong được phục vụ bạn một lần nữa.\r\n\r\nCũng được', '2001/1/1 0:00', 'DML', '', '');
