INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(11, 4, '[WellBe] 请求您的预订。%v', '感谢您访问 WellBe。\r\n您接受了预订请求。\r\n\r\n请在 15 小时内通过管理屏幕回复预订可用性。 请注意，您的预订将在截止时间后自动取消。\r\n\r\n- 预订信息\r\n预订号码：%v\r\n预订日期和时间：%v \r\n预订菜单：%v \r\n人数：%v\r\n预订金额：%v %v\r\n\r\n请通过店铺管理界面进行预订。\r\nhttps://shop.wellbe-asia.com/booking/\r\n\r\n取消政策\r\n请在预订确认后通过 https://shop.wellbe-asia.com/terms 与我们联系，了解我们的取消政策。', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(12, 4, '[WellBe] 您的预订已被接受。%v', '感谢您一直使用 WellBe。 \r\n我们已接受您的预订。 \r\n\r\n■预约信息\r\n预约号码: %v\r\n预约日期和时间: %v \r\n预约菜单: %v\r\n预约人数: %v 姓名\r \n预订金额：%v %v\r\n\r\n\r\n■取消政策\r\n预订确认后因店铺情况而取消的详细信息，https://shop.wellbe-asia.com/terms 条款。', '2001/1/1 0:00', 'DML', '', '');
