INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(16, 4, '[WellBe] 請完成付款，否則預訂將被取消 %v', '感谢您使用 WellBe。\r\n您的预订尚未确定。\r\n请在付款期限内完成付款。 如果您尚未付款，请注意，您的预订将在截止日期后自动取消。 请确认详细信息后，在预约的日期和时间到店。\r\n*此电子邮件仅供分发，因此即使您回复，也不会发送到沙龙。\r\n\r\n可以在下面付款。\r\nhttps://wellbe-asia.com/zh/booking/checkout/%v/\r\n\r\n■预约信息\r\n预订号码：%v\r\n预订店：%v\r\n地址：%v\r\n预订日期和时间：%v（当地时间）\r\n\r\n■支付金额\r\n%v %v\r\n\r\n■付款期限\r\n%v（当地时间）\r\n\r\n*如果您尚未完成付款，请注意，您的预订将在截止日期后自动取消。\r\n如果您已经付款，请忽略。\r\n\r\n\r\n\r\n■取消政策\r\n有关取消详情，请参阅。https://wellbe-asia.com/cancellation-policy\r\n\r\n\r\nWellBe', '2001/1/1 0:00', 'DML', '', '');
