INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(1, 5, '[WellBe] 您的預訂已被接受。%v', '感謝您對 WellBe 的關注。\r\n我們已接受您的預訂如下。\r\n請檢查詳細信息並在預訂日期和時間前往沙龍。\r\n請注意，這封電子郵件適用於只限送貨，即使回覆也不會送到沙龍。\r\n\r\n- 預訂資訊\r\n 預訂號碼：%v\r\n 預訂商店：%v\r\n地址：%v \r\n 預訂日期及時間：%v(當地時間)\r\n\r\n%v- 取消政策\r \n如果您想查看取消詳細信息，請訪問。https://wellbe-asia.com/cancellation-policy\r\n\r\n\r\nWellBe', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(2, 5, '[WellBe] 您對 %v 的預訂已被取消。 %v', '感謝您使用 WellBe。\r\n我們已接受您的預訂取消。\r\n我們期待再次見到您。\r\n\r\n■預約信息\r\n\r\n預約號碼：%v\r\n預訂日期和時間：%v（當地時間）\r\n預訂店：%v\r\n\r\n\r\n■退款金額\r\n\r\n%v %v\r\n\r\n分解\r\n-付款金額：%v %v\r\n- 取消費用：%v %v\r\n\r\n*退款將透過系統依序處理。\r\n此外，對於信用卡付款，由於信用卡公司的情況，取消可能需要幾個工作天才能在您的帳單上處理。\r\n注意。\r\n\r\nWellBe', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(3, 5, '[WellBe] 已取消您的預訂 %v. %v', '感謝您造訪WellBe。\r\n由於付款截止日期已過，我們已取消您的預訂。\r\n\r\n\r\n- 預訂資訊\r\n\r\n預訂號碼：% v\r \n預訂日期和時間：%v(當地時間)\r\n預訂門市：%v\r\n\r\n\r\n我們期待再次為您服務。\r\n\r \n\r\恩威爾貝', '2001/1/1 0:00', 'DML', '', '');
