INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(6, 5, '[WellBe] 要求您的預訂。 %v', '感謝您對WellBe 的關注。\r\n我們已按如下方式接受您的預訂。\r\n 如果您回复此電子郵件，該電子郵件將不會發送到沙龍。\r\n我們將在15天內與您聯繫確認或拒絕預訂的時間發送至您註冊的電子郵件地址。\r\n如果您在 15 小時後沒有收到電子郵件，請檢查您的電子郵件，因為它可能已被分類到您的垃圾郵件中。 \r\n\r\n- 預訂資訊\r\n 預訂編號：%v\r\n 預訂商店：%v\r\n 地址：%v\r\n 預訂日期和時間：%v(本地時間)\r\n\r\n\r\n%v- 取消政策\r\n請訪問。https://wellbe-asia.com/cancellation-policy 如果您已經支付預訂費用，請不要取消預訂。\r\n\r\n\r\nWellBe', '2001/1/1 0:00', 'DML', '', '');
