INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(8, 5, '[WellBe] 提款手續已完成。', '您從WellBe 的提款已被接受。\r\n\r\n感謝您迄今為止使用WellBe。\r\n我們期待再次見到您。\r\n\r\n請注意，這封電子郵件是從專用電子郵件地址。 請注意，我們將無法回覆您的回覆。\r\n請注意，我們將無法回覆您的回覆。\r\n\r\n\r\nWellBe', '2001/1/1 0:00', 'DML', '', '');
