INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(15, 5, '[WellBe] 請留下評論！ %v!', '感謝您使用 WellBe。\r\n\r\n我們正在尋求您的回饋。\r\n如果您願意，我們很樂意發布對您的體驗的評論。\r\n您有 30 天的時間您訪問的日期以提交評論。\r\n您可以在下面輸入您的反饋。\r\n\r\nhttps://wellbe-asia.com/account/review\r\n\r\n我們期待再次為您服務。\r\n\r\nWellBe', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(16, 5, '[WellBe] 请完成付款，否则预订将被取消 %v', '感謝您使用 WellBe。\r\n您的預訂尚未確定。\r\n請在付款期限內完成付款。 如果您尚未付款，請注意，您的預訂將在截止日期後自動取消。 請確認詳細資料後，在預約的日期和時間到店。\r\n*此電子郵件僅供分發，因此即使您回复，也不會發送到沙龍。\r\n\r\n可以在下面付款。\r\nhttps://wellbe-asia.com/zh-TW/booking/checkout/%v/\r\n\r\n■預約信息\r\n預約號碼：%v\r\n預訂店：%v\r\n地址：%v\r\n預訂日期和時間：%v（當地時間）\r\n\r\n■支付金額\r\n%v %v\r\n\r\n■付款期限\r\n%v（當地時間）\r\n\r\n*如果您尚未完成付款，請注意，您的預訂將在截止日期後自動取消。\r\n如果您已經付款，請忽略。\r\n\r\n\r\n\r\n■取消政策\r\n取消詳情，請參閱 https://wellbe-asia.com/cancellation-policy\r\n\r\n\r\nWellBe', '2001/1/1 0:00', 'DML', '', '');
