INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(5, 6, '[WellBe] 결제가 실패했습니다. %v', 'WellBe를 이용해 주셔서 감사합니다. \ r \ n 이하의 예약 결제가 신용 카드 회사 또는 결제 회사의 사정으로 실패했습니다. \r\n불편을 끼쳐드려 죄송합니다. 다시 결제해 주세요. \r\n\r\n■예약 정보\r\n\r\n예약 번호:%v\r\n예약 점포:%v\r\n\r\n\r\n■요금\r\ n\r\n%v %v\r\n\r\n\r\n결제 화면은 메뉴 > 계정 정보에서 볼 수 있습니다.', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(6, 6, '[WellBe] 요청 예약을 접수했습니다. %v', 'WellBe를 이용해 주셔서 감사합니다. \r\n아래와 같이 예약을 접수했습니다. \r\n※본 메일은 전달 전용이므로, 회신해 주셔도 살롱에는 도착하지 않습니다. \r\n\r\n이 살롱은 요청 예약입니다. \r\n15 시간 이내에 예약 가능 여부의 연락을 등록 이메일 주소로 보내드립니다. \r\n또한, 15시간 경과해도 메일이 도착하지 않는 경우는, 스팸 메일에 배분될 가능성이 있으므로 확인해 주세요.\r\n\r\n\r\n■ 예약 정보 \r\n 예약 번호 : %v\r\n 예약 점포 : %v\r\n 주소 : %v\r\n 예약 일시 : %v (현지 시간)\r\n\r\n\r\n%v■취소 정책\r\n취소에 대한 자세한 내용은 https://wellbe-asia.com/cancellation-policy 를 참조하십시오. \r\n\r\n\r\nWellBe\r\n', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(7, 6, '[WellBe] 귀하의 계정 등록이 완료되었습니다.', 'WellBe에 등록해 주셔서 감사합니다. \r\n\r\n이하의 URL로부터 회원 페이지에 로그인해, 등록 정보 및 예약 상황등의 확인을 부탁합니다. \r\n\r\n▼회원 페이지\r\nhttps://wellbe-asia.com/ko/account/\r\n\r\n▼살롱 찾기는 이쪽으로부터\r\nhttps://wellbe -asia.com/\r\n\r\n※본 메일은 송신 전용 메일 주소로부터 전달되고 있습니다. 회신해도 대답 할 수 없으므로 양해 바랍니다. \r\n\r\n\r\nWellBe', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(8, 6, '[WellBe]탈퇴 절차가 완료되었습니다.', '탈퇴 절차가 완료되었습니다. \r\n\r\nWellBe를 이용해 주셔서 감사합니다. \r\n\r\n※본 메일은 송신 전용 메일 주소로부터 전달되고 있습니다. 회신해도 대답 할 수 없으므로 양해 바랍니다. \r\n※탈퇴 후, 자동적으로 메일 전달은 정지됩니다만, 정보 갱신의 타이밍에 의해 탈퇴 후에도 메일이 몇통 전달되는 경우가 있습니다. \r\n\r\n\r\nWellBe', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(9, 6, '[WellBe] 예약을 취소했습니다. %v. %v', 'WellBe를 이용해 주셔서 감사합니다. \ r \ n 정말 죄송합니다. 고객 예약은 점포 측의 사정으로 예약이 취소되었습니다. \r\n또한 이용 기다리고 있습니다. \r\n\r\n■예약 정보\r\n\r\n예약 번호:%v\r\n예약 일시:%v(현지 시간)\r\n예약 점포:%v\r\n \r\n\r\n■환불 금액\r\n\r\n%v %v\r\n\r\n내역\r\n-결제 금액: %v %v\r\n-취소 요금：%v %v\r\n\r\n※환불 처리는 시스템에서 순차적으로 실시합니다. \r\n또한, 신용카드 결제의 경우는, 카드 회사의 사정으로 취소 처리가 며칠 후가 되는 경우가 있습니다. \r\n양해 바랍니다. \r\n\r\n\r\n\r\n--\r\n■취소 이유\r\n%v\r\n\r\n\r\nWellBe', '2001/1/1 0:00', 'DML', '', '');
