INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(6, 7, '[WellBe] ขอจองของคุณ %v', 'ขอขอบคุณที่ให้ความสนใจ WellBe\r\nเราได้ยอมรับการจองของคุณดังนี้\r\n หากคุณตอบกลับอีเมลนี้ จะไม่ถูกส่งไปยังร้านเสริมสวย\r\nโปรดตรวจสอบอีเมลของคุณหากคุณไม่ได้รับอีเมลหลังจากผ่านไป 15 ชั่วโมง เนื่องจากอีเมลอาจถูกจัดเรียงไว้ในอีเมลขยะแล้ว \r\n\r\n- ข้อมูลการจอง\r\n หมายเลขการจอง：%v\r\n ร้านจอง：%v\r\n ที่อยู่：%v\r\n วันที่และเวลาจอง：%v(ท้องถิ่น เวลา)\r\n\r\n\r\n%v- นโยบายการยกเลิก\r\nโปรดไปที่ https://wellbe-asia com/cancellation-policy หากคุณได้ชำระเงินสำหรับการจองของคุณแล้ว โปรดอย่ายกเลิก\r\n\r\n\r\nWellBe\r\n', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(7, 7, '[WellBe] การลงทะเบียนบัญชีของคุณเสร็จสมบูรณ์แล้ว', 'ขอขอบคุณที่ลงทะเบียนกับ WellBe\r\n\r\nโปรดเข้าสู่หน้าสมาชิกจาก URL ต่อไปนี้เพื่อตรวจสอบข้อมูลการลงทะเบียนและสถานะการจองของคุณ\r\n\r\nหน้าสมาชิก\r\nhttps://wellbe-asia.com/account/\r\n\r\nคลิกที่นี่เพื่อค้นหาร้านเสริมสวย\r\nhttps://wellbe-asia.com/\r\n\r\nอีเมลนี้ส่งจากที่อยู่อีเมลเฉพาะ โปรดทราบว่าเราไม่สามารถตอบกลับคำตอบของคุณได้\r\n\r\n\r\nWellBe', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(8, 7, '[WellBe] ขั้นตอนการถอนเงินเสร็จสมบูรณ์', 'การถอนตัวของคุณจาก WellBe ได้รับการยอมรับแล้ว\r\n\r\nขอขอบคุณที่ใช้ WellBe จนถึงตอนนี้\r\nเราหวังว่าจะได้พบคุณอีกครั้ง\r\n\r\nโปรดทราบว่าอีเมลนี้ส่งจาก ที่อยู่อีเมลเฉพาะ โปรดทราบว่าเราไม่สามารถตอบกลับการตอบกลับของคุณได้\r\nโปรดทราบว่าเราจะไม่สามารถตอบกลับการตอบกลับของคุณได้\r\n\r\n\r\nWellBe', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(9, 7, '[WellBe] ยกเลิกการจองของคุณไปที่ %v. %v', 'ขอบคุณสำหรับการเยี่ยมชม WellBe\r\nเราเสียใจที่ต้องแจ้งให้คุณทราบว่าการจองของคุณถูกยกเลิกโดยร้านเสริมสวย\r\nเราขออภัยในความไม่สะดวกที่เกิดขึ้น\r\nเราหวังว่าจะได้ให้บริการคุณอีกครั้ง\r\n\ r\n- ข้อมูลการจอง\r\n\r\nหมายเลขการจอง: %v\r\nวันที่และเวลาจอง: %v (เวลาท้องถิ่น)\r\nร้านจอง：%v\r\n\r\n\r \n- จำนวนเงินคืน\r\n\r\n%v %v\r\n\r\nรายละเอียด\r\n-จำนวนเงินที่ชำระ: %v %v\r\n-ค่าธรรมเนียมการยกเลิก: %v %v\ r\n\r\nการคืนเงินจะดำเนินการตามลำดับโดยระบบ\r\nนอกจากนี้ ในกรณีของการชำระเงินด้วยบัตรเครดิต การประมวลผลการยกเลิกในใบแจ้งยอดบัตรเครดิตอาจใช้เวลาหลายวันทำการ เนื่องจากความสะดวกของบริษัทบัตรเครดิต .\r\nโปรดเข้าใจ\r\n\r\n\r\n\r\n--\r\n- เหตุผลในการยกเลิก\r\n%v\r\n\r\n\r\ WellBe', '2001/1/1 0:00', 'DML', '', '');
