INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(11, 9, '[WellBe] Minta pemesanan Anda. %v', 'Terima kasih telah mengunjungi WellBe.\r\nAnda menyetujui permintaan pemesanan.\r\n\r\nHarap balas ketersediaan pemesanan dalam waktu 15 jam dari layar manajemen. Harap dicatat bahwa pemesanan Anda akan dibatalkan secara otomatis setelah batas waktu.\r\n\r\n- Informasi Pemesanan\r\nNomor pemesanan: %v\r\nTanggal dan waktu pemesanan: %v \r\nMenu pemesanan：%v \r\nJumlah orang: %v\r\nJumlah pemesanan: %v %v\r\n\r\nSilakan melakukan pemesanan dari layar manajemen toko.\r\nhttps://shop.wellbe-asia.com/booking/\r\n\r\nKebijakan Pembatalan\r\nSilakan hubungi kami di https://shop.wellbe-asia.com/terms untuk kebijakan pembatalan kami setelah pemesanan Anda dikonfirmasi.', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(12, 9, '[WellBe] Reservasi Anda telah diterima. %v', 'Terima kasih karena selalu menggunakan WellBe. \r\nKami telah menerima reservasi Anda. \r\n\r\n■Informasi reservasi\r\nNomor reservasi: %v\r\nTanggal dan waktu reservasi: %v \r\nMenu reservasi: %v\r\nJumlah orang yang dipesan: %v Nama\r \nJumlah reservasi: %v %v\r\n\r\n\r\n■Kebijakan pembatalan\r\nUntuk detail pembatalan karena keadaan toko setelah reservasi dikonfirmasi, https://shop.wellbe-asia.com/terms', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(13, 9, '[WellBe] Reservasi Anda telah dikonfirmasi. %v', 'Terima kasih telah menggunakan WellBe.\r\nReservasi permintaan Anda telah diterima.\r\nSilakan periksa detailnya dan datang ke toko pada tanggal dan waktu yang Anda pesan.\r\n*Email ini hanya untuk distribusi, jadi meskipun Anda membalasnya, email tersebut tidak akan dikirimkan ke salon.\r\n\r\n- Informasi reservasi\r\nNomor reservasi: %v\r\nToko reservasi:%v\r\nAlamat:%v\r\nTanggal dan waktu reservasi: %v (waktu setempat)\r\n\r\n%v\r\n\r\n- Kebijakan pembatalan\r\nUntuk detail pembatalan, silakan lihat https://wellbe-asia.com/cancellation-policy.\r\n\r\n\r\nWellBe', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(14, 9, '[WellBe] Permintaan pengguna diterima [%v].', 'Permintaan diterima dari pengguna.\r\n\r\n- Nama:\r\n%v\r\n\r\n- Alamat email:\r\n%v\r\n\r\n - Nomor Reservasi: %v\r\n%v\r\n\r\n- Jenis pertanyaan: %v\r\n%v\r\n\r\n- Subjek:\r\n%v \r\n\r\n- Isi:\r\n%v\r\n\r\n\r\nWellBe\r\n', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(15, 9, '[WellBe] Mohon masukan pengalaman Anda %v!', 'Terima kasih telah menggunakan WellBe.\r\n\r\nKami menantikan masukan dari Anda.\r\nJika Anda mau, kami akan dengan senang hati mengirimkan ulasan tentang pengalaman Anda.\r\nAnda memiliki waktu 30 hari sejak tanggal kunjungan Anda untuk mengirimkan ulasan.\r\nAnda dapat memasukkan masukan Anda di bawah.\r\n\r\nhttps://wellbe-asia.com/account/review\r\n\r\nKami menantikannya untuk melayani Anda lagi.\r\n\r\nWellBe', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(16, 9, '[WellBe] Sila lengkapkan pembayaran atau tempahan akan dibatalkan %v', 'Terima kasih telah menggunakan WellBe.\r\nReservasi Anda belum diselesaikan.\r\nHarap selesaikan pembayaran sebelum batas waktu pembayaran. Jika Anda belum melakukan pembayaran, harap diperhatikan bahwa reservasi Anda akan otomatis dibatalkan setelah batas waktu yang ditentukan. Silakan periksa detailnya dan datang ke toko pada tanggal dan waktu yang Anda pesan.\r\n*Email ini hanya untuk distribusi, jadi meskipun Anda membalasnya, email tersebut tidak akan dikirimkan ke salon.\r\n\r\nPembayaran dapat dilakukan di bawah ini.\r\nhttps://wellbe-asia.com/id/booking/checkout/%v/\r\n\r\n- Informasi reservasi\r\nNomor reservasi: %v\r\nToko reservasi:%v\r\nAlamat:%v\r\nTanggal dan waktu reservasi: %v (waktu setempat)\r\n\r\n- Jumlah pembayaran\r\n%v %v\r\n\r\n- Batas waktu pembayaran\r\n%v (waktu setempat)\r\n\r\n*Jika Anda belum menyelesaikan pembayaran, harap diperhatikan bahwa reservasi Anda akan otomatis dibatalkan setelah batas waktu yang ditentukan.\r\nJika Anda sudah melakukan pembayaran, harap abaikan saja.\r\n\r\n\r\n\r\n- Kebijakan pembatalan\r\nUntuk detail pembatalan, silakan lihat https://wellbe-asia.com/cancellation-policy \r\n\r\n\r\nWellBe', '2001/1/1 0:00', 'DML', '', '');
INSERT INTO wellbe_common.c_mail_template(mail_template_cd, language_cd, subject, body, create_datetime, create_function, update_datetime, update_function) VALUES(17, 9, '[WellBe] Reservasi Anda telah dibatalkan. %v', 'Terima kasih karena selalu menggunakan WellBe. \r\nReservasi pelanggan telah dibatalkan. \r\n\r\n■Informasi reservasi\r\nNomor reservasi: %v\r\nTanggal dan waktu reservasi: %v \r\nMenu reservasi: %v\r\nJumlah orang yang dipesan: %v \r\ nJumlah reservasi: %v %v\r\nTanggal dan waktu pembatalan: %v\r\n\r\nWellBe', '2001/1/1 0:00', 'DML', '', '');
